import React, { useState, useEffect } from 'react';

const RACE_DURATION = 60000; // 60 seconds
const UPDATE_INTERVAL = 1000; // 1 second
const TRACK_LENGTH = 100;

const CryptoChickenRace = () => {
  const [cryptos, setCryptos] = useState([
    { id: 'bitcoin', name: 'Bitcoin', emoji: 'üêî', color: '#f7931a', position: 0, startPrice: 0, currentPrice: 0 },
    { id: 'ethereum', name: 'Ethereum', emoji: 'üêì', color: '#62688f', position: 0, startPrice: 0, currentPrice: 0 },
    { id: 'dogecoin', name: 'Dogecoin', emoji: 'üê•', color: '#c3a634', position: 0, startPrice: 0, currentPrice: 0 },
  ]);
  const [raceStarted, setRaceStarted] = useState(false);
  const [winner, setWinner] = useState(null);
  const [timeLeft, setTimeLeft] = useState(RACE_DURATION / 1000);

  useEffect(() => {
    if (raceStarted) {
      const interval = setInterval(() => {
        updatePrices();
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(interval);
            endRace();
            return 0;
          }
          return prevTime - 1;
        });
      }, UPDATE_INTERVAL);

      return () => clearInterval(interval);
    }
  }, [raceStarted]);

  const startRace = async () => {
    setRaceStarted(true);
    setWinner(null);
    setTimeLeft(RACE_DURATION / 1000);
    
    // Fetch initial prices
    await fetchInitialPrices();
  };

  const fetchInitialPrices = async () => {
    const ids = cryptos.map(crypto => crypto.id).join(',');
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${ids}&vs_currencies=usd`);
      const data = await response.json();
      
      setCryptos(prevCryptos => 
        prevCryptos.map(crypto => ({
          ...crypto,
          position: 0,
          startPrice: data[crypto.id].usd,
          currentPrice: data[crypto.id].usd
        }))
      );
    } catch (error) {
      console.error('Error fetching initial prices:', error);
      // Use fallback prices if API call fails
      setCryptos(prevCryptos => 
        prevCryptos.map(crypto => ({
          ...crypto,
          position: 0,
          startPrice: Math.random() * 1000,
          currentPrice: Math.random() * 1000
        }))
      );
    }
  };

  const updatePrices = () => {
    setCryptos(prevCryptos => 
      prevCryptos.map(crypto => {
        const priceChange = (Math.random() - 0.5) * 2; // Random price change between -1% and 1%
        const newPrice = crypto.currentPrice * (1 + priceChange / 100);
        const totalChange = ((newPrice - crypto.startPrice) / crypto.startPrice) * 100;
        const newPosition = Math.min(TRACK_LENGTH, Math.max(0, totalChange));
        
        return {
          ...crypto,
          position: newPosition,
          currentPrice: newPrice,
        };
      })
    );
  };

  const endRace = () => {
    setRaceStarted(false);
    const winner = cryptos.reduce((prev, current) => 
      (prev.position > current.position) ? prev : current
    );
    setWinner(winner);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-pink-500">
      <div className="p-8 bg-white rounded-lg shadow-xl max-w-4xl w-full">
        <h2 className="text-4xl font-bold mb-6 text-center text-gray-800">Crypto Chicken Race</h2>
        <div className="mb-6 flex justify-center">
          <button 
            onClick={startRace} 
            disabled={raceStarted}
            className="px-6 py-3 bg-blue-500 text-white rounded-full hover:bg-blue-600 disabled:bg-gray-400 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-110"
          >
            {raceStarted ? 'Race in Progress' : 'Start Race'}
          </button>
          {raceStarted && <span className="ml-4 text-2xl font-semibold">Time left: {timeLeft}s</span>}
        </div>
        <div className="mb-6 space-y-4">
          {cryptos.map((crypto) => (
            <div key={crypto.id} className="bg-gray-100 rounded-lg p-4 shadow">
              <div className="flex items-center">
                <div className="w-24 font-semibold">{crypto.name}</div>
                <div className="flex-grow bg-gray-300 h-10 rounded-full relative overflow-hidden">
                  <div
                    className="h-full absolute top-0 left-0 flex items-center justify-center text-white font-bold rounded-full"
                    style={{
                      width: `${crypto.position}%`,
                      backgroundColor: crypto.color,
                      transition: 'width 1s linear'
                    }}
                  >
                    {crypto.emoji}
                  </div>
                </div>
                <div className="w-32 text-right font-mono">
                  ${crypto.currentPrice.toFixed(2)}
                </div>
              </div>
            </div>
          ))}
        </div>
        {winner && (
          <div className="text-2xl font-bold text-center bg-yellow-300 p-4 rounded-lg animate-pulse">
            Winner: {winner.name} {winner.emoji} üèÜ
          </div>
        )}
      </div>
    </div>
  );
};

export default CryptoChickenRace;
