import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import * as roomService from '../services/roomService';
import LoadingScreen from './LoadingScreen';

const PREDICTION_DURATION = 60000; // 1 minute
const UPDATE_INTERVAL = 5000; // 5 seconds

const CryptoPricePredictor = ({ roomCode, playerName, isHost, selectedCrypto }) => {
  const [crypto, setCrypto] = useState(null);
  const [players, setPlayers] = useState([]);
  const [gameStarted, setGameStarted] = useState(false);
  const [winner, setWinner] = useState(null);
  const [timeLeft, setTimeLeft] = useState(PREDICTION_DURATION / 1000);
  const [isLoading, setIsLoading] = useState(true);
  const [prediction, setPrediction] = useState('');
  const [priceHistory, setPriceHistory] = useState([]);
  const [startTime, setStartTime] = useState(null);
  const [startPrice, setStartPrice] = useState(null);
  const [endTime, setEndTime] = useState(null);
  const [endPrice, setEndPrice] = useState(null);
  const [amount, setAmount] = useState('');

  useEffect(() => {
    const fetchCryptoData = async () => {
      setIsLoading(true);
      try {
        const response = await fetch(`https://api.coingecko.com/api/v3/coins/${selectedCrypto}`);
        const data = await response.json();
        setCrypto({
          id: data.id,
          name: data.name,
          symbol: data.symbol,
          image: data.image.small,
          currentPrice: data.market_data.current_price.usd
        });
        setPriceHistory([{
          timestamp: new Date().getTime(),
          price: data.market_data.current_price.usd
        }]);
      } catch (error) {
        console.error('Error fetching crypto data:', error);
      } finally {
        setIsLoading(false);
      }
    };
    fetchCryptoData();
  }, [selectedCrypto]);

  useEffect(() => {
    const interval = setInterval(() => {
      const updatedPlayers = roomService.getRoomPlayers(roomCode);
      if (updatedPlayers) {
        setPlayers(updatedPlayers);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [roomCode]);

  useEffect(() => {
    if (gameStarted) {
      const interval = setInterval(() => {
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(interval);
            endGame();
            return 0;
          }
          return prevTime - 1;
        });

        fetchCurrentPrice();
      }, UPDATE_INTERVAL);

      return () => clearInterval(interval);
    }
  }, [gameStarted]);

  const fetchCurrentPrice = async () => {
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${crypto.id}&vs_currencies=usd`);
      const data = await response.json();
      const currentPrice = data[crypto.id].usd;
      const currentTime = new Date().getTime();

      setPriceHistory(prevHistory => [
        ...prevHistory,
        { timestamp: currentTime, price: currentPrice }
      ]);

      setCrypto(prevCrypto => ({
        ...prevCrypto,
        currentPrice: currentPrice
      }));
    } catch (error) {
      console.error('Error fetching current price:', error);
    }
  };

  const startGame = async () => {
    setGameStarted(true);
    setWinner(null);
    setTimeLeft(PREDICTION_DURATION / 1000);
    const startTimestamp = new Date();
    setStartTime(startTimestamp);
    setStartPrice(crypto.currentPrice);
    roomService.startGame(roomCode, startTimestamp, crypto.currentPrice);

    // Fetch the exact price at the start time
    const exactStartPrice = await fetchPriceAtTimestamp(crypto.id, startTimestamp);
    setStartPrice(exactStartPrice);
  };

  const endGame = async () => {
    setGameStarted(false);
    const endTimestamp = new Date();
    setEndTime(endTimestamp);

    // Fetch the exact price at the end time
    const exactEndPrice = await fetchPriceAtTimestamp(crypto.id, endTimestamp);
    setEndPrice(exactEndPrice);

    const winningPlayer = players.reduce((closest, player) => {
      const currentDiff = Math.abs(player.prediction - exactEndPrice);
      const closestDiff = Math.abs(closest.prediction - exactEndPrice);
      return currentDiff < closestDiff ? player : closest;
    });

    setWinner(winningPlayer);
    
    setPlayers(prevPlayers => 
      prevPlayers.map(player => ({
        ...player,
        winner: player.name === winningPlayer.name
      }))
    );

    roomService.endGame(roomCode, endTimestamp, exactEndPrice, winningPlayer);
  };

  const fetchPriceAtTimestamp = async (cryptoId, timestamp) => {
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/coins/${cryptoId}/history?date=${formatDate(timestamp)}`);
      const data = await response.json();
      return data.market_data.current_price.usd;
    } catch (error) {
      console.error('Error fetching price at timestamp:', error);
      return null;
    }
  };

  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    return `${date.getDate()}-${date.getMonth() + 1}-${date.getFullYear()}`;
  };

  const submitPrediction = () => {
    if (prediction && !isNaN(prediction) && amount && !isNaN(amount)) {
      roomService.updatePlayerPrediction(roomCode, playerName, parseFloat(prediction), parseFloat(amount));
      setPrediction('');
      setAmount('');
    }
  };

  if (isLoading || !crypto || priceHistory.length === 0) {
    return <LoadingScreen />;
  }

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleString();
  };

  const CustomTooltip = ({ active, payload, label }) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-gray-800 p-2 border border-cyan-500 rounded">
          <p className="text-cyan-300">Time: {formatTimestamp(label)}</p>
          <p className="text-green-400">Price: ${payload[0].value.toFixed(2)}</p>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-cyan-300 p-4">
      <div className="w-full max-w-6xl bg-gray-800 rounded-lg shadow-xl border border-cyan-500 p-8">
        <h2 className="text-4xl font-bold mb-6 text-center text-cyan-400 animate-pulse">Crypto Price Predictor 2077</h2>
        <div className="mb-4 text-center">Room Code: <span className="font-bold text-pink-500">{roomCode}</span></div>
        <div className="mb-6 flex justify-center">
          {isHost && !gameStarted && (
            <button 
              onClick={startGame} 
              className="px-6 py-3 bg-pink-600 text-white rounded-full hover:bg-pink-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-110 shadow-lg shadow-pink-500/50"
            >
              Start Race
            </button>
          )}
          {gameStarted && <span className="text-2xl font-semibold text-yellow-400">Time left: {timeLeft}s</span>}
        </div>
        <div className="mb-6 bg-gray-700 rounded-lg p-4 shadow-lg border border-cyan-700">
          <div className="flex items-center justify-between mb-4">
            <div className="flex items-center">
              <img src={crypto.image} alt={crypto.name} className="w-8 h-8 mr-2" />
              <div className="font-semibold text-cyan-300">{crypto.name} ({crypto.symbol.toUpperCase()})</div>
            </div>
            <div className="font-mono text-yellow-400">
              Current Price: ${crypto.currentPrice.toFixed(2)}
            </div>
          </div>
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={priceHistory}>
              <CartesianGrid strokeDasharray="3 3" stroke="#444" />
              <XAxis 
                dataKey="timestamp" 
                tickFormatter={(unixTime) => new Date(unixTime).toLocaleTimeString()} 
                stroke="#8884d8"
              />
              <YAxis stroke="#8884d8" />
              <Tooltip content={<CustomTooltip />} />
              <Legend />
              <Line type="monotone" dataKey="price" stroke="#00ff00" name="Price" dot={false} />
              {players.map((player, index) => (
                <Line
                  key={player.name}
                  type="monotone"
                  dataKey={() => player.prediction}
                  stroke={`hsl(${index * 50}, 70%, 50%)`}
                  strokeDasharray="5 5"
                  name={`${player.name}'s Prediction`}
                />
              ))}
            </LineChart>
          </ResponsiveContainer>
        </div>
        <div className="mb-6">
          <h3 className="text-xl font-bold mb-2 text-pink-400">Players</h3>
          <div className="space-y-2">
            {players.map((player, index) => (
              <div key={index} className="flex items-center justify-between bg-gray-700 p-2 rounded border border-pink-700">
                <span className="text-cyan-300">{player.name}</span>
                <span className="text-yellow-400">
                  {player.prediction ? `$${player.prediction.toFixed(2)}` : 'No prediction yet'}
                </span>
                <span className="text-green-400">
                  {player.amount ? `$${player.amount.toFixed(2)}` : 'No amount placed'}
                </span>
                {winner && player.winner && <span className="ml-2 text-2xl">🏆</span>}
              </div>
            ))}
          </div>
        </div>
        {!gameStarted && (
          <div className="mb-6">
            <input
              type="number"
              step="0.01"
              placeholder="Enter your price prediction"
              value={prediction}
              onChange={(e) => setPrediction(e.target.value)}
              className="w-full p-2 border rounded bg-gray-700 text-cyan-300 border-cyan-500 placeholder-cyan-600 focus:outline-none focus:ring-2 focus:ring-pink-500 mb-2"
            />
            <input
              type="number"
              step="0.01"
              placeholder="Enter amount to place"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              className="w-full p-2 border rounded bg-gray-700 text-cyan-300 border-cyan-500 placeholder-cyan-600 focus:outline-none focus:ring-2 focus:ring-pink-500 mb-2"
            />
            <button
              onClick={submitPrediction}
              className="w-full px-4 py-2 bg-cyan-600 text-white rounded hover:bg-cyan-700 transition duration-300 ease-in-out"
            >
              Submit Prediction and Place Amount
            </button>
          </div>
        )}
        {winner && (
          <div className="text-xl font-bold text-center bg-yellow-600 text-black p-4 rounded-lg animate-pulse">
            <h3 className="text-2xl mb-2">Winner: {winner.name} 🏆</h3>
            <div className="grid grid-cols-2 gap-4 text-lg">
              <div>
                <p>Start Time: {formatTimestamp(startTime)}</p>
                <p>Start Price: ${startPrice.toFixed(2)}</p>
              </div>
              <div>
                <p>End Time: {formatTimestamp(endTime)}</p>
                <p>End Price: ${endPrice.toFixed(2)}</p>
              </div>
            </div>
            <div className="mt-4">
              <p>Winning Prediction: ${winner.prediction.toFixed(2)}</p>
              <p>Difference: ${Math.abs(winner.prediction - endPrice).toFixed(2)}</p>
              <p>Accuracy: {(100 - Math.abs((winner.prediction - endPrice) / endPrice * 100)).toFixed(2)}%</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CryptoPricePredictor;
