import React, { useState, useEffect } from 'react';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select"
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card"

const CryptoPriceTracker = () => {
  const [cryptoData, setCryptoData] = useState([]);
  const [selectedCrypto, setSelectedCrypto] = useState('bitcoin');
  const [price, setPrice] = useState(null);

  useEffect(() => {
    const fetchCryptoList = async () => {
      try {
        const response = await fetch('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false');
        const data = await response.json();
        setCryptoData(data);
      } catch (error) {
        console.error('Error fetching crypto list:', error);
      }
    };

    fetchCryptoList();
  }, []);

  useEffect(() => {
    const fetchPrice = async () => {
      try {
        const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${selectedCrypto}&vs_currencies=usd`);
        const data = await response.json();
        setPrice(data[selectedCrypto].usd);
      } catch (error) {
        console.error('Error fetching price:', error);
      }
    };

    if (selectedCrypto) {
      fetchPrice();
      const interval = setInterval(fetchPrice, 60000); // Update every minute
      return () => clearInterval(interval);
    }
  }, [selectedCrypto]);

  const handleCryptoChange = (value) => {
    setSelectedCrypto(value);
  };

  return (
    <Card className="w-96">
      <CardHeader>
        <CardTitle>Crypto Price Tracker</CardTitle>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          <Select onValueChange={handleCryptoChange} value={selectedCrypto}>
            <SelectTrigger>
              <SelectValue placeholder="Select a cryptocurrency" />
            </SelectTrigger>
            <SelectContent>
              {cryptoData.map((crypto) => (
                <SelectItem key={crypto.id} value={crypto.id}>
                  {crypto.name}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
          {price !== null && (
            <div className="text-2xl font-bold">
              Price: ${price.toFixed(2)} USD
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
};

export default CryptoPriceTracker;
