import React, { useState } from 'react';
import { v4 as uuidv4 } from 'uuid';
import * as roomService from '../services/roomService';

const CryptoRaceLobby = ({ onJoinRoom }) => {
  const [roomCode, setRoomCode] = useState('');
  const [playerName, setPlayerName] = useState('');
  const [error, setError] = useState('');

  const createRoom = () => {
    if (playerName.trim() === '') {
      setError('Please enter your name');
      return;
    }
    const newRoomCode = uuidv4().substr(0, 6).toUpperCase();
    roomService.createRoom(newRoomCode, playerName);
    onJoinRoom(newRoomCode, playerName, true);
  };

  const joinRoom = () => {
    if (playerName.trim() === '' || roomCode.trim() === '') {
      setError('Please enter your name and room code');
      return;
    }
    const success = roomService.joinRoom(roomCode.toUpperCase(), playerName);
    if (success) {
      onJoinRoom(roomCode.toUpperCase(), playerName, false);
    } else {
      setError('Unable to join room. It might not exist or the race has already started.');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-purple-500 to-pink-500">
      <div className="p-8 bg-white rounded-lg shadow-xl max-w-md w-full">
        <h2 className="text-3xl font-bold mb-6 text-center text-gray-800">Crypto Chicken Race</h2>
        <div className="space-y-4">
          <input
            type="text"
            placeholder="Your Name"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
            className="w-full p-2 border rounded"
          />
          <input
            type="text"
            placeholder="Room Code (for joining)"
            value={roomCode}
            onChange={(e) => setRoomCode(e.target.value)}
            className="w-full p-2 border rounded"
          />
          <div className="flex space-x-4">
            <button
              onClick={createRoom}
              className="flex-1 px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600"
            >
              Create Room
            </button>
            <button
              onClick={joinRoom}
              className="flex-1 px-4 py-2 bg-green-500 text-white rounded hover:bg-green-600"
            >
              Join Room
            </button>
          </div>
          {error && <p className="text-red-500 text-center">{error}</p>}
        </div>
      </div>
    </div>
  );
};

export default CryptoRaceLobby;
