import React from 'react';

const LoadingScreen = ({ cryptos }) => {
  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-cyan-300">
      <h2 className="text-4xl font-bold mb-8 text-center text-cyan-400 animate-pulse">
        Initializing Crypto Race 2077
      </h2>
      <div className="relative w-full max-w-3xl h-24 overflow-hidden">
        <div className="absolute inset-0 flex items-center justify-center">
          <div className="w-full h-1 bg-cyan-700"></div>
        </div>
        <div className="absolute inset-0 flex items-center">
          <div className="w-full flex justify-around animate-slide">
            {cryptos.concat(cryptos).map((crypto, index) => (
              <img 
                key={index} 
                src={crypto.image} 
                alt={crypto.name} 
                className="w-12 h-12 rounded-full bg-gray-800 p-1 border-2 border-pink-500"
              />
            ))}
          </div>
        </div>
      </div>
      <p className="mt-8 text-lg text-pink-400">Loading crypto data...</p>
    </div>
  );
};

export default LoadingScreen;
