import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const RACE_DURATION = 60000; // 60 seconds
const UPDATE_INTERVAL = 1000; // 1 second
const TRACK_LENGTH = 100;

const CryptoChickenRace = () => {
  const [cryptos, setCryptos] = useState([
    { id: 'bitcoin', name: 'Bitcoin', color: '#f7931a', position: 0, priceHistory: [] },
    { id: 'ethereum', name: 'Ethereum', color: '#62688f', position: 0, priceHistory: [] },
    { id: 'dogecoin', name: 'Dogecoin', color: '#c3a634', position: 0, priceHistory: [] },
  ]);
  const [raceStarted, setRaceStarted] = useState(false);
  const [winner, setWinner] = useState(null);
  const [timeLeft, setTimeLeft] = useState(RACE_DURATION / 1000);

  useEffect(() => {
    if (raceStarted) {
      const interval = setInterval(() => {
        updatePrices();
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(interval);
            endRace();
            return 0;
          }
          return prevTime - 1;
        });
      }, UPDATE_INTERVAL);

      return () => clearInterval(interval);
    }
  }, [raceStarted]);

  const startRace = () => {
    setRaceStarted(true);
    setWinner(null);
    setTimeLeft(RACE_DURATION / 1000);
    setCryptos(cryptos.map(crypto => ({ ...crypto, position: 0, priceHistory: [] })));
  };

  const updatePrices = async () => {
    const ids = cryptos.map(crypto => crypto.id).join(',');
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${ids}&vs_currencies=usd`);
      const data = await response.json();
      
      setCryptos(prevCryptos => 
        prevCryptos.map(crypto => {
          const newPrice = data[crypto.id].usd;
          const lastPrice = crypto.priceHistory[crypto.priceHistory.length - 1]?.price || newPrice;
          const priceChange = ((newPrice - lastPrice) / lastPrice) * 100;
          const newPosition = Math.min(TRACK_LENGTH, Math.max(0, crypto.position + priceChange));
          
          return {
            ...crypto,
            position: newPosition,
            priceHistory: [...crypto.priceHistory, { time: Date.now(), price: newPrice }],
          };
        })
      );
    } catch (error) {
      console.error('Error fetching prices:', error);
    }
  };

  const endRace = () => {
    setRaceStarted(false);
    const winner = cryptos.reduce((prev, current) => 
      (prev.position > current.position) ? prev : current
    );
    setWinner(winner);
  };

  return (
    <div className="p-4 bg-white rounded shadow-md max-w-4xl mx-auto mt-10">
      <h2 className="text-2xl font-bold mb-4">Crypto Chicken Race</h2>
      <div className="mb-4">
        <button 
          onClick={startRace} 
          disabled={raceStarted}
          className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 disabled:bg-gray-400"
        >
          {raceStarted ? 'Race in Progress' : 'Start Race'}
        </button>
        {raceStarted && <span className="ml-4">Time left: {timeLeft}s</span>}
      </div>
      <div className="mb-4 h-40 relative bg-gray-200">
        {cryptos.map((crypto, index) => (
          <div 
            key={crypto.id}
            style={{
              position: 'absolute',
              left: `${crypto.position}%`,
              top: `${index * 33}%`,
              transition: 'left 1s linear',
            }}
          >
            üêî {crypto.name}
          </div>
        ))}
      </div>
      {winner && (
        <div className="mb-4 text-xl font-bold">
          Winner: {winner.name} üèÜ
        </div>
      )}
      <ResponsiveContainer width="100%" height={300}>
        <LineChart data={cryptos[0].priceHistory}>
          <XAxis dataKey="time" type="number" domain={['dataMin', 'dataMax']} tickFormatter={(unixTime) => new Date(unixTime).toLocaleTimeString()} />
          <YAxis />
          <Tooltip labelFormatter={(label) => new Date(label).toLocaleTimeString()} />
          <Legend />
          {cryptos.map(crypto => (
            <Line 
              key={crypto.id} 
              type="monotone" 
              dataKey="price" 
              stroke={crypto.color} 
              name={crypto.name} 
              data={crypto.priceHistory} 
            />
          ))}
        </LineChart>
      </ResponsiveContainer>
    </div>
  );
};

export default CryptoChickenRace;
