import React, { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import Web3Modal from "web3modal";
import WalletConnectProvider from "@walletconnect/web3-provider";

const RACE_DURATION = 60; // 60 seconds
const UPDATE_INTERVAL = 1000; // 1 second
const TRACK_LENGTH = 100;
const STAKE_AMOUNT = ethers.parseEther("0.001"); // 0.001 ETH stake

const networks = {
  sepolia: { 
    chainId: '0xaa36a7',
    chainName: 'Sepolia Testnet',
    nativeCurrency: { name: 'Sepolia Ether', symbol: 'SEP', decimals: 18 },
    rpcUrls: ['https://sepolia.infura.io/v3/YOUR_INFURA_ID'],
    blockExplorerUrls: ['https://sepolia.etherscan.io']
  },
  arbitrumGoerli: {
    chainId: '0x66eed',
    chainName: 'Arbitrum Goerli',
    nativeCurrency: { name: 'Arbitrum Goerli Ether', symbol: 'AGOR', decimals: 18 },
    rpcUrls: ['https://goerli-rollup.arbitrum.io/rpc'],
    blockExplorerUrls: ['https://goerli.arbiscan.io']
  },
  // Add more networks as needed
};

const providerOptions = {
  walletconnect: {
    package: WalletConnectProvider,
    options: {
      infuraId: "YOUR_INFURA_ID" // Replace with your Infura ID
    }
  }
};

const web3Modal = new Web3Modal({
  network: "sepolia",
  cacheProvider: true,
  providerOptions
});

const CryptoChickenRace = () => {
  const [account, setAccount] = useState(null);
  const [network, setNetwork] = useState('sepolia');
  const [provider, setProvider] = useState(null);
  const [cryptos, setCryptos] = useState([]);
  const [players, setPlayers] = useState([]);
  const [raceStarted, setRaceStarted] = useState(false);
  const [winner, setWinner] = useState(null);
  const [timeLeft, setTimeLeft] = useState(RACE_DURATION);
  const [roomCode, setRoomCode] = useState('');
  const [playerName, setPlayerName] = useState('');
  const [isHost, setIsHost] = useState(false);
  const [staked, setStaked] = useState(false);

  useEffect(() => {
    if (web3Modal.cachedProvider) {
      connectWallet();
    }
  }, []);

  useEffect(() => {
    if (raceStarted) {
      const interval = setInterval(() => {
        updateRace();
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(interval);
            endRace();
            return 0;
          }
          return prevTime - 1;
        });
      }, UPDATE_INTERVAL);

      return () => clearInterval(interval);
    }
  }, [raceStarted]);

  const connectWallet = async () => {
    try {
      const web3Provider = await web3Modal.connect();
      const library = new ethers.BrowserProvider(web3Provider);
      const accounts = await library.listAccounts();
      const network = await library.getNetwork();
      setProvider(library);
      if (accounts) setAccount(accounts[0]);
      setNetwork(network.name);
    } catch (error) {
      console.error("Could not connect to wallet:", error);
    }
  };

  const disconnectWallet = async () => {
    await web3Modal.clearCachedProvider();
    setAccount(null);
    setProvider(null);
  };

  const switchNetwork = async (networkName) => {
    if (!provider) {
      console.error("Wallet not connected");
      return;
    }

    const networkConfig = networks[networkName];

    try {
      await provider.send('wallet_switchEthereumChain', [{ chainId: networkConfig.chainId }]);
      setNetwork(networkName);
    } catch (switchError) {
      if (switchError.code === 4902) {
        try {
          await provider.send('wallet_addEthereumChain', [networkConfig]);
          setNetwork(networkName);
        } catch (addError) {
          console.error("Failed to add network:", addError);
        }
      } else {
        console.error("Failed to switch network:", switchError);
      }
    }
  };

  const createRoom = async () => {
    if (!playerName) {
      alert("Please enter your name");
      return;
    }
    const newRoomCode = Math.random().toString(36).substring(2, 8).toUpperCase();
    setRoomCode(newRoomCode);
    setIsHost(true);
    // In a real app, you'd create the room on a backend service here
    setPlayers([{ name: playerName, selection: null, staked: false }]);
  };

  const joinRoom = async () => {
    if (!playerName || !roomCode) {
      alert("Please enter your name and room code");
      return;
    }
    // In a real app, you'd verify the room exists and join it on a backend service here
    setPlayers(prevPlayers => [...prevPlayers, { name: playerName, selection: null, staked: false }]);
  };

  const stakeAmount = async () => {
    if (!account || !provider) {
      alert("Please connect your wallet first");
      return;
    }
    try {
      const signer = await provider.getSigner();
      const balance = await provider.getBalance(account);
      
      if (balance.lt(STAKE_AMOUNT)) {
        alert(`Insufficient balance. You need at least ${ethers.formatEther(STAKE_AMOUNT)} ETH to stake.`);
        return;
      }

      const tx = await signer.sendTransaction({
        to: roomCode, // This should be a smart contract address in a real app
        value: STAKE_AMOUNT
      });

      await tx.wait();
      setStaked(true);
      setPlayers(prevPlayers => 
        prevPlayers.map(p => 
          p.name === playerName ? { ...p, staked: true } : p
        )
      );
    } catch (error) {
      console.error("Error staking amount:", error);
      alert("Failed to stake amount. Please try again.");
    }
  };

  const selectCrypto = (cryptoId) => {
    setPlayers(prevPlayers => 
      prevPlayers.map(p => 
        p.name === playerName ? { ...p, selection: cryptoId } : p
      )
    );
  };

  const startRace = async () => {
    if (players.some(player => !player.selection || !player.staked)) {
      alert('All players must select a cryptocurrency and stake before starting the race!');
      return;
    }
    setRaceStarted(true);
    setWinner(null);
    setTimeLeft(RACE_DURATION);
    await fetchInitialPrices();
  };

  const fetchInitialPrices = async () => {
    const selectedCryptos = players.map(player => player.selection);
    const ids = [...new Set(selectedCryptos)].join(',');
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${ids}&vs_currencies=usd`);
      const data = await response.json();
      
      setCryptos(prevCryptos => 
        prevCryptos.map(crypto => ({
          ...crypto,
          position: 0,
          startPrice: data[crypto.id]?.usd || 0,
          currentPrice: data[crypto.id]?.usd || 0
        }))
      );
    } catch (error) {
      console.error('Error fetching initial prices:', error);
    }
  };

  const updateRace = () => {
    setCryptos(prevCryptos => 
      prevCryptos.map(crypto => {
        if (players.some(player => player.selection === crypto.id)) {
          const priceChange = (Math.random() - 0.5) * 2; // Random price change between -1% and 1%
          const newPrice = crypto.currentPrice * (1 + priceChange / 100);
          const totalChange = ((newPrice - crypto.startPrice) / crypto.startPrice) * 100;
          const newPosition = Math.min(TRACK_LENGTH, Math.max(0, totalChange));
          
          return {
            ...crypto,
            position: newPosition,
            currentPrice: newPrice,
          };
        }
        return crypto;
      })
    );
  };

  const endRace = () => {
    setRaceStarted(false);
    const winningCrypto = cryptos.reduce((prev, current) => 
      (prev.position > current.position) ? prev : current
    );
    setWinner(winningCrypto);
    
    // In a real app, you'd distribute winnings here
  };

  const formatAddress = (address) => {
    if (typeof address === 'string' && address.length >= 10) {
      return `${address.substring(0, 6)}...${address.substring(address.length - 4)}`;
    }
    return address || 'Not connected';
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-cyan-300">
      <div className="p-8 bg-gray-800 rounded-lg shadow-xl max-w-4xl w-full border border-cyan-500">
        <h2 className="text-4xl font-bold mb-6 text-center text-cyan-400 animate-pulse">Crypto Chicken Race 2077</h2>
        
        {!account ? (
          <button onClick={connectWallet} className="w-full px-4 py-2 bg-cyan-600 text-white rounded hover:bg-cyan-700 transition duration-300 ease-in-out">
            Connect Wallet
          </button>
        ) : (
          <div className="mb-4 flex items-center justify-between bg-gray-700 p-2 rounded border border-cyan-500">
            <span className="text-cyan-300">Connected: {formatAddress(account)}</span>
            <select
              value={network}
              onChange={(e) => switchNetwork(e.target.value)}
              className="mx-2 p-1 bg-gray-800 text-cyan-300 rounded border border-cyan-500"
            >
              {Object.keys(networks).map((networkName) => (
                <option key={networkName} value={networkName}>
                  {networks[networkName].chainName}
                </option>
              ))}
            </select>
            <button onClick={disconnectWallet} className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition duration-300 ease-in-out">
              Disconnect
            </button>
          </div>
        )}

        {account && !roomCode && (
          <div className="mt-4 space-y-4">
            <input
              type="text"
              placeholder="Your Name"
              value={playerName}
              onChange={(e) => setPlayerName(e.target.value)}
              className="w-full p-2 bg-gray-700 text-cyan-300 rounded border border-cyan-500"
            />
            <div className="flex space-x-4">
              <button onClick={createRoom} className="flex-1 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition duration-300 ease-in-out">
                Create Room
              </button>
              <input
                type="text"
                placeholder="Room Code"
                value={roomCode}
                onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
                className="flex-1 p-2 bg-gray-700 text-cyan-300 rounded border border-cyan-500"
              />
              <button onClick={joinRoom} className="flex-1 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition duration-300 ease-in-out">
                Join Room
              </button>
            </div>
          </div>
        )}

        {account && roomCode && (
          <>
            <div className="mt-4 text-center">
              Room Code: <span className="font-bold text-pink-500">{roomCode}</span>
            </div>
            
            {!raceStarted && (
              <div className="mt-4 space-y-4">
                {!staked && (
                  <button 
                    onClick={stakeAmount} 
                    className="w-full px-6 py-3 bg-yellow-600 text-white rounded-full hover:bg-yellow-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-105 shadow-lg shadow-yellow-500/50"
                  >
                    Stake 0.001 ETH
                  </button>
                )}
                <select
                  value={players.find(p => p.name === playerName)?.selection || ''}
                  onChange={(e) => selectCrypto(e.target.value)}
                  className="w-full p-2 bg-gray-700 text-cyan-300 rounded border border-cyan-500"
                >
                  <option value="">Select a crypto</option>
                  {cryptos.map(crypto => (
                    <option key={crypto.id} value={crypto.id}>{crypto.name}</option>
                  ))}
                </select>
                {isHost && (
                  <button 
                    onClick={startRace} 
                    className="w-full px-6 py-3 bg-pink-600 text-white rounded-full hover:bg-pink-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-105 shadow-lg shadow-pink-500/50"
                  >
                    Start Race
                  </button>
                )}
              </div>
            )}

            {raceStarted && (
              <div className="mt-4">
                <div className="text-2xl font-semibold text-yellow-400 text-center">Time left: {timeLeft}s</div>
                <div className="mt-4 space-y-4">
                  {cryptos.filter(crypto => players.some(player => player.selection === crypto.id)).map((crypto) => (
                    <div key={crypto.id} className="bg-gray-700 rounded-lg p-4 shadow-lg border border-cyan-700">
                      <div className="flex items-center">
                        <div className="w-24 font-semibold text-cyan-300">{crypto.name}</div>
                        <div className="flex-grow bg-gray-600 h-10 rounded-full relative overflow-hidden">
                          <div
                            className="h-full absolute top-0 left-0 flex items-center justify-center text-white font-bold rounded-full"
                            style={{
                              width: `${crypto.position}%`,
                              backgroundColor: `#${Math.floor(Math.random()*16777215).toString(16)}`,
                              transition: 'width 1s linear'
                            }}
                          >
                            üêî
                          </div>
                        </div>
                        <div className="w-32 text-right font-mono text-green-400">
                          ${crypto.currentPrice.toFixed(2)}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="mt-6">
              <h3 className="text-xl font-bold mb-2 text-pink-400">Players</h3>
              <div className="space-y-2">
                {players.map((player, index) => (
                  <div key={index} className="flex items-center justify-between bg-gray-700 p-2 rounded border border-pink-700">
                    <span className="text-cyan-300">{player.name}</span>
                    <span className="text-yellow-400">
                      {player.selection ? cryptos.find(c => c.id === player.selection)?.name : 'Not selected'}
                    </span>
                    {player.staked && <span className="text-green-400">Staked</span>}
                    {winner && player.selection === winner.id && <span className="text-2xl">üèÜ</span>}
                  </div>
                ))}
              </div>
            </div>

            {winner && (
              <div className="mt-6 text-2xl font-bold text-center bg-yellow-600 text-black p-4 rounded-lg animate-pulse">
                Winner: {winner.name} üèÜ
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default CryptoChickenRace;
