import React, { useState, useEffect } from 'react';
import Web3Modal from "web3modal";
import { BrowserProvider, Network } from "ethers";
import WalletConnectProvider from "@walletconnect/web3-provider";

const NETWORK_ID = 11155111; // Sepolia testnet
const NETWORK_NAME = "Sepolia";

const providerOptions = {
  walletconnect: {
    package: WalletConnectProvider,
    options: {
      infuraId: "DY7wIf74UTtinAu5IgM6hFuNNmpYwPEYQuDrfG0dFF+niHwDM3n2Gw" // Replace with your Infura ID
    }
  }
};

const web3Modal = new Web3Modal({
  network: NETWORK_NAME.toLowerCase(),
  cacheProvider: true,
  providerOptions
});

const WalletConnection = ({ onConnect }) => {
  const [account, setAccount] = useState(null);
  const [network, setNetwork] = useState(null);
  const [provider, setProvider] = useState(null);

  const connectWallet = async () => {
    try {
      const provider = await web3Modal.connect();
      const library = new BrowserProvider(provider);
      const accounts = await library.listAccounts();
      const network = await library.getNetwork();
      
      if (accounts && accounts.length > 0) {
        const address = accounts[0].address || accounts[0];
        setAccount(address);
        setNetwork(network);
        setProvider(provider);
        onConnect(address, library);
      } else {
        console.error("No accounts found");
      }
    } catch (error) {
      console.error("Could not connect to wallet:", error);
    }
  };

  const disconnectWallet = async () => {
    await web3Modal.clearCachedProvider();
    setAccount(null);
    setNetwork(null);
    setProvider(null);
  };

  const switchNetwork = async () => {
    if (!provider) return;
    try {
      await provider.request({
        method: "wallet_switchEthereumChain",
        params: [{ chainId: `0x${NETWORK_ID.toString(16)}` }],
      });
    } catch (switchError) {
      // This error code indicates that the chain has not been added to MetaMask.
      if (switchError.code === 4902) {
        try {
          await provider.request({
            method: "wallet_addEthereumChain",
            params: [
              {
                chainId: `0x${NETWORK_ID.toString(16)}`,
                chainName: NETWORK_NAME,
                rpcUrls: [`https://${NETWORK_NAME.toLowerCase()}.infura.io/v3/YOUR_INFURA_ID`],
                nativeCurrency: {
                  name: "ETH",
                  symbol: "ETH",
                  decimals: 18
                },
                blockExplorerUrls: [`https://${NETWORK_NAME.toLowerCase()}.etherscan.io`]
              },
            ],
          });
        } catch (addError) {
          console.error("Error adding the network:", addError);
        }
      } else {
        console.error("Error switching the network:", switchError);
      }
    }
  };

  useEffect(() => {
    if (web3Modal.cachedProvider) {
      connectWallet();
    }
  }, []);

  useEffect(() => {
    if (provider) {
      provider.on("accountsChanged", connectWallet);
      provider.on("chainChanged", connectWallet);
      return () => {
        provider.removeListener("accountsChanged", connectWallet);
        provider.removeListener("chainChanged", connectWallet);
      };
    }
  }, [provider]);

  const formatAddress = (address) => {
    if (typeof address === 'string' && address.length > 10) {
      return `${address.substring(0, 6)}...${address.substring(address.length - 4)}`;
    }
    return address;
  };

  const isCorrectNetwork = network && network.chainId === NETWORK_ID;

  return (
    <div className="mb-4">
      {account ? (
        <div className="flex flex-col items-start bg-gray-700 p-2 rounded border border-cyan-500">
          <div className="flex w-full justify-between items-center mb-2">
            <span className="text-cyan-300">
              Connected: {formatAddress(account)}
            </span>
            <button onClick={disconnectWallet} className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition duration-300 ease-in-out">
              Disconnect
            </button>
          </div>
          {!isCorrectNetwork && (
            <button 
              onClick={switchNetwork} 
              className="w-full px-4 py-2 bg-yellow-600 text-white rounded hover:bg-yellow-700 transition duration-300 ease-in-out"
            >
              Switch to {NETWORK_NAME}
            </button>
          )}
        </div>
      ) : (
        <button onClick={connectWallet} className="w-full px-4 py-2 bg-cyan-600 text-white rounded hover:bg-cyan-700 transition duration-300 ease-in-out">
          Connect Wallet
        </button>
      )}
    </div>
  );
};

export default WalletConnection;
