import React, { useState, useEffect } from 'react';
import * as roomService from '../services/roomService';

const RACE_DURATION = 60000; // 60 seconds
const UPDATE_INTERVAL = 1000; // 1 second
const TRACK_LENGTH = 100;

const CryptoChickenRace = ({ roomCode, playerName, isHost }) => {
  const [cryptos, setCryptos] = useState([]);
  const [players, setPlayers] = useState([]);
  const [raceStarted, setRaceStarted] = useState(false);
  const [winners, setWinners] = useState([]);
  const [timeLeft, setTimeLeft] = useState(RACE_DURATION / 1000);

  useEffect(() => {
    const fetchCryptos = async () => {
      const fetchedCryptos = await roomService.fetchCryptocurrencies();
      setCryptos(fetchedCryptos.map(crypto => ({
        ...crypto,
        position: 0,
        startPrice: 0,
        currentPrice: 0,
        color: `#${Math.floor(Math.random()*16777215).toString(16)}` // Random color
      })));
    };
    fetchCryptos();
  }, []);

  useEffect(() => {
    const interval = setInterval(() => {
      const updatedPlayers = roomService.getRoomPlayers(roomCode);
      if (updatedPlayers) {
        setPlayers(updatedPlayers);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [roomCode]);

  useEffect(() => {
    if (raceStarted) {
      const interval = setInterval(() => {
        updatePrices();
        setTimeLeft((prevTime) => {
          if (prevTime <= 1) {
            clearInterval(interval);
            endRace();
            return 0;
          }
          return prevTime - 1;
        });
      }, UPDATE_INTERVAL);

      return () => clearInterval(interval);
    }
  }, [raceStarted]);

  const startRace = async () => {
    if (players.some(player => player.selection === null)) {
      alert('All NetRunners must select a cryptocurrency before starting the race!');
      return;
    }
    setRaceStarted(true);
    setWinners([]);
    setTimeLeft(RACE_DURATION / 1000);
    roomService.startRace(roomCode);
    await fetchInitialPrices();
  };

  const fetchInitialPrices = async () => {
    const selectedCryptos = players.map(player => player.selection);
    const ids = [...new Set(selectedCryptos)].join(',');
    try {
      const response = await fetch(`https://api.coingecko.com/api/v3/simple/price?ids=${ids}&vs_currencies=usd`);
      const data = await response.json();
      
      setCryptos(prevCryptos => 
        prevCryptos.map(crypto => ({
          ...crypto,
          position: 0,
          startPrice: data[crypto.id]?.usd || Math.random() * 1000,
          currentPrice: data[crypto.id]?.usd || Math.random() * 1000
        }))
      );
    } catch (error) {
      console.error('Error fetching initial prices:', error);
      setCryptos(prevCryptos => 
        prevCryptos.map(crypto => ({
          ...crypto,
          position: 0,
          startPrice: Math.random() * 1000,
          currentPrice: Math.random() * 1000
        }))
      );
    }
  };

  const updatePrices = () => {
    setCryptos(prevCryptos => 
      prevCryptos.map(crypto => {
        if (players.some(player => player.selection === crypto.id)) {
          const priceChange = (Math.random() - 0.5) * 2; // Random price change between -1% and 1%
          const newPrice = crypto.currentPrice * (1 + priceChange / 100);
          const totalChange = ((newPrice - crypto.startPrice) / crypto.startPrice) * 100;
          const newPosition = Math.min(TRACK_LENGTH, Math.max(0, totalChange));
          
          return {
            ...crypto,
            position: newPosition,
            currentPrice: newPrice,
          };
        }
        return crypto;
      })
    );
  };

  const endRace = () => {
    setRaceStarted(false);
    const winningCrypto = cryptos.reduce((prev, current) => 
      (prev.position > current.position) ? prev : current
    );
    
    const winningPlayers = players.filter(player => player.selection === winningCrypto.id);
    setWinners(winningPlayers);
  };

  const selectCrypto = (playerName, cryptoId) => {
    roomService.updatePlayerSelection(roomCode, playerName, cryptoId);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-900 text-cyan-300">
      <div className="p-8 bg-gray-800 rounded-lg shadow-xl max-w-4xl w-full border border-cyan-500">
        <h2 className="text-4xl font-bold mb-6 text-center text-cyan-400 animate-pulse">Crypto Race 2077</h2>
        <div className="mb-4 text-center">Room Code: <span className="font-bold text-pink-500">{roomCode}</span></div>
        <div className="mb-6 flex justify-center">
          {isHost && !raceStarted && (
            <button 
              onClick={startRace} 
              className="px-6 py-3 bg-pink-600 text-white rounded-full hover:bg-pink-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-110 shadow-lg shadow-pink-500/50"
            >
              Initiate Race
            </button>
          )}
          {raceStarted && <span className="text-2xl font-semibold text-yellow-400">Time left: {timeLeft}s</span>}
        </div>
        <div className="mb-6 space-y-4">
          {cryptos.filter(crypto => players.some(player => player.selection === crypto.id)).map((crypto) => (
            <div key={crypto.id} className="bg-gray-700 rounded-lg p-4 shadow-lg border border-cyan-700">
              <div className="flex items-center">
                <img src={crypto.image} alt={crypto.name} className="w-8 h-8 mr-2" />
                <div className="w-24 font-semibold text-cyan-300">{crypto.name}</div>
                <div className="flex-grow bg-gray-600 h-10 rounded-full relative overflow-hidden">
                  <div
                    className="h-full absolute top-0 left-0 flex items-center justify-center text-white font-bold rounded-full"
                    style={{
                      width: `${crypto.position}%`,
                      backgroundColor: crypto.color,
                      transition: 'width 1s linear'
                    }}
                  >
                    {crypto.symbol.toUpperCase()}
                  </div>
                </div>
                <div className="w-32 text-right font-mono text-green-400">
                  ${crypto.currentPrice.toFixed(2)}
                </div>
              </div>
            </div>
          ))}
        </div>
        <div className="mb-6">
          <h3 className="text-xl font-bold mb-2 text-pink-400">NetRunners</h3>
          <div className="space-y-2">
            {players.map((player, index) => (
              <div key={index} className="flex items-center justify-between bg-gray-700 p-2 rounded border border-pink-700">
                <span className="text-cyan-300">{player.name}</span>
                {!raceStarted && (
                  <select
                    value={player.selection || ''}
                    onChange={(e) => selectCrypto(player.name, e.target.value)}
                    disabled={playerName !== player.name}
                    className="p-1 border rounded bg-gray-800 text-cyan-300 border-cyan-500"
                  >
                    <option value="">Select a crypto</option>
                    {cryptos.map(crypto => (
                      <option key={crypto.id} value={crypto.id}>{crypto.name}</option>
                    ))}
                  </select>
                )}
                {raceStarted && (
                  <span className="text-yellow-400">{player.selection ? cryptos.find(c => c.id === player.selection)?.name : 'Not selected'}</span>
                )}
              </div>
            ))}
          </div>
        </div>
        {winners.length > 0 && (
          <div className="text-2xl font-bold text-center bg-yellow-600 text-black p-4 rounded-lg animate-pulse">
            {winners.length === 1 ? 'Champion NetRunner:' : 'Champion NetRunners:'}
            <div className="mt-2">
              {winners.map((winner, index) => (
                <div key={index} className="text-xl">
                  {winner.name} 🏆 
                  <span className="text-sm">
                    ({cryptos.find(c => c.id === winner.selection)?.name})
                  </span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CryptoChickenRace;
